for (let item of DATA) {
  let itemOrder = item.order;
  if (ORDER_LIST.indexOf(itemOrder) === -1) ORDER_LIST.push(itemOrder);
}

const FAMILY_LIST = [];
for (let item of DATA) {
  let itemFamily = item.family;
  if (FAMILY_LIST.indexOf(itemFamily) === -1) FAMILY_LIST.push(itemFamily);
}

const LOCATION_LIST = [];
for (let item of DATA) {
  let itemLocation = item.location;
  let locationList = itemLocation.split(",");
  for (let locationItem of locationList) {
    locationItem = locationItem.trim();
    if (LOCATION_LIST.indexOf(locationItem) === -1) LOCATION_LIST.push(locationItem);
  }
}

