"use strict";

const SysAuditLogAbl = require("../../abl/sys-audit-log-abl.js");

class SysAuditLogController {
  logRecordDelete(ucEnv) {
    return SysAuditLogAbl.deleteAuditLogRecordsBeforeTime(ucEnv.getUri().getAwid(), ucEnv.getDtoIn());
  }

  logRecordList(ucEnv) {
    return SysAuditLogAbl.logRecordList(ucEnv.getUri().getAwid(), ucEnv.getDtoIn());
  }

  logRecordTransfer(ucEnv) {
    return SysAuditLogAbl.transfer();
  }

  logRecordGetTransferStatus(ucEnv) {
    return SysAuditLogAbl.getTransferStatus();
  }

  logRecordPrivilegedList(ucEnv) {
    return SysAuditLogAbl.logRecordPrivilegedList(ucEnv.getDtoIn());
  }

  logRecordPrivilegedDelete(ucEnv) {
    return SysAuditLogAbl.privilegedDelete(ucEnv.getDtoIn());
  }
}

module.exports = new SysAuditLogController();
