const { BaseError } = require("uu_appg01_core-utils");

class CacheError extends BaseError {
  constructor(message, cause, data) {
    super(message, cause);
    this.data = data;
  }
};

const ERRORS = {
  CacheError,
  CacheConfigurationError: class extends CacheError {},
  CacheNotFoundError: class extends CacheError {},
  CacheHandlerNotFoundError: class extends CacheError {},
};

module.exports = ERRORS;