"use strict";

const { BaseError } = require("uu_appg01_core-utils");

class Cache {
  constructor(code, config = {}) {
    this._code = code;
    this._config = config;
  }

  get code() {
    return this._code;
  }

  set code(_code) {
    throw new BaseError("Cache code may not be set after cache creation.");
  }

  get config() {
    return this._config;
  }

  set config(_config) {
    throw new BaseError("Config may not be replaced.");
  }
}

module.exports = Cache;
