"use strict";

const Cache = require("../cache.js");

const CACHE_REALIZATION_NAME = "disabled";
const CACHE_REALIZATION_POLICY = "disabled";

/**
 * @implements {CacheRealization}
 */
class DisabledCache extends Cache {
  constructor(code, config = {}) {
    super(code, config);
    this._config = {};
    this._code = code;
    this._cacheSize = 0;
  }

  set(_awid, _key, value, _opts = {}) {
    return null;
  }

  has(_awid, _key) {
    return false;
  }

  get(_awid, _key) {
    return null;
  }

  delete(_awid, _key) {
    return null;
  }

  keys(_awid = null) {
    return [];
  }

  values(_awid = null) {
    return [];
  }

  entries(_awid = null) {
    return [];
  }

  clear(_awid = null) {}

  static get REALIZATION_NAME() {
    return CACHE_REALIZATION_NAME;
  }

  static get REALIZATION_POLICY() {
    return CACHE_REALIZATION_POLICY;
  }
}

module.exports = DisabledCache;
