"use strict";

const UseCaseError = require("./use-case-error.js");
const DIAGNOSTIC_ERROR_PREFIX = `${UseCaseError.ERROR_PREFIX}server/sys/`;

const Generate = {
  UC_CODE: `${DIAGNOSTIC_ERROR_PREFIX}generateDiagnosticReport/`,
  InvalidDtoInError: class extends UseCaseError {
    constructor(dtoOut, paramMap = {}, cause = null) {
      super({ dtoOut, paramMap, cause, status: 400 });
      this.code = `${Generate.UC_CODE}invalidDtoIn`;
      this.message = "DtoIn is not valid.";
    }
  },
  GeneratingOfReportFailed: class extends UseCaseError {
    constructor(dtoOut, paramMap = {}, cause = null) {
      super({ dtoOut, paramMap, cause });
      this.code = `${Generate.UC_CODE}generatingOfReportFailed`;
      this.message = "Generating of diagnostic report failed."
    }
  }
};

module.exports = {
  Generate
};
