"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

class _IsNotNull extends Matcher {
  constructor(...params) {
    super("isNotNull", ...params);
  }

  // Process and evaluate incoming value.
  match(value) {
    let result = new ValidationResult();
    if (value === null) {
      result.addInvalidValueError("isNotNull.e001", "Value must not be null.");
    }
    return result;
  }
}

function isNotNull(...params) {
  let matcher = new _IsNotNull(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator();
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = isNotNull;
