"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const APP_KEY_URN_PREFIX = "urn:uuappkey:";
const IDENTIFIER = "[a-zA-Z0-9_]";
const VENDOR = `(${IDENTIFIER}{1,32})`;
const APP = `(${IDENTIFIER}{1,32})`;
const SUB_APP = `(${IDENTIFIER}{1,32})`;
const ASID = `(${IDENTIFIER}{1,32})`;
const AWID = `(${IDENTIFIER}{1,32})`;

const APP_KEY_REGEXP = new RegExp(
  `^(?:${APP_KEY_URN_PREFIX})?${VENDOR}(?:\-(?:${APP}(?:\-(?:${SUB_APP}(?:\\/(?:${ASID}(?:\-(?:${AWID})?)?)?)?)?)?)?)?$`
);

class _UuAppKey extends Matcher {

  constructor(...params) {
    super("uuAppKey", ...params);
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == null) {
      return result;
    }

    if (typeof value != "string" || !value.match(APP_KEY_REGEXP)) {
      result.addInvalidValueError("uuAppKey.e001", "The value must be uuAppKey.");
    }

    return result;
  }
}

function UuAppKey(...params) {
  let matcher = new _UuAppKey(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UuAppKey;
