"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const UU_APP_PROFILE_REGEXP = /^[a-zA-Z]{3,64}$/;

class _UuAppProfile extends Matcher {

  constructor(...params) {
    super("uuAppProfile", ...params);
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == null) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(UU_APP_PROFILE_REGEXP)) {
        result.addInvalidValueError("uuAppProfile.e001", "The value must be uuAppProfile.");
      }
    } else {
      result.addInvalidTypeError("uuAppProfile.e001", "The value must be uuAppProfile.");
    }

    return result;
  }

}

function UuAppProfile(...params) {
  let matcher = new _UuAppProfile(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UuAppProfile;
