"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const APP_VERSION_REGEXP = /^(\d+\.){2}\d+(\-[0-9A-Za-z\-]+(\.[0-9A-Za-z\-]+)*)?(\+[0-9A-Za-z\-]+(\.[0-9A-Za-z\-]+)*)?$/;

class _UuAppVersion extends Matcher {

  constructor(...params) {
    super("uuAppVersion", ...params);
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == null) {
      return result;
    }

    if (typeof value != "string" || !value.match(APP_VERSION_REGEXP)) {
      result.addInvalidValueError("uuAppVersion.e001", "The value must be uuAppVersion.");
    }

    return result;
  }

}

function UuAppVersion(...params) {
  let matcher = new _UuAppVersion(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UuAppVersion;
