"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const AUTHORIZATION_STRATEGY_TYPE = `const authorizationStrategyType = oneOf(["uuIdentityList", "roleGroupInterface", "artifact", "boundArtifact"]);`;

class _UuAuthorizationStrategy extends Matcher {

  constructor(...params) {
    super("authorizationStrategy", ...params);
    this.validator = new Validator(AUTHORIZATION_STRATEGY_TYPE);
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == null) {
      return result;
    }

    let childResult = this.validator.validate("authorizationStrategyType", value);
    if (!childResult.isValid()) {
      result.addInvalidValueError("authorizationStrategy.e001", `The value must be authorizationStrategy.`);
    }

    return result;
  }
}

function UuAuthorizationStrategy(...params) {
  let matcher = new _UuAuthorizationStrategy(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UuAuthorizationStrategy;
