const OidcToken = require("..//oidc-token");
const BasicAuth = require("../basic-auth");


function getTokenFromParams(authStrategy, oidcCredentialsFile, basicCredentialsFile){
    switch(authStrategy){
      case "basic":
        return new BasicAuth(basicCredentialsFile);
      case "oidc":
        if(oidcCredentialsFile){
          return new OidcToken(__dirname, oidcCredentialsFile);
        }else{
          return new OidcToken(__dirname);
        }
    }
    return null;
}

module.exports = {getTokenFromParams};
