"use strict";

const { AuthorizationResult } = require("uu_appg01_core-authorization");

class WorkspaceAuthorizationResult extends AuthorizationResult {
  constructor(data = {}) {
    super(data);
    this._authorizationError = data.authorizationError || null;
  }

  /**
   * Returns information of the authorization error.
   * @returns {object} with the proper authorization error info
   */
  getAuthorizationError() {
    return this._authorizationError;
  }
}

module.exports = WorkspaceAuthorizationResult;
